targets.expr:
count (
    count_over_time (
        {app="freechat-main", level="ACCESS"}
        | pattern `<_> [<_>] <_> <_> - <_>|<username>|<_>`
        | keep username [$__range]
    )
)

count_over_time (
    {app="freechat-main", level="ACCESS"}
    |= "|/api/v1/chat/send/stream" [$__range]
)

count_over_time (
    {app="freechat-main", level="ACCESS"}
    |= "|/api/v1/character|" [$__range]
)

topk(5, 
    sum by (name) (
        count_over_time (
            {app="freechat-main", level="PERF", class="ChatApi", method="firstPackageReceived"}
            | pattern `<_>|"<name>",<_>` [$__range]
        )
    )
)

topk(5, 
    sum by (api) (
        count_over_time (
            {app="freechat-main", level="ACCESS"}
            |= "|/api"
            | label_format api=`{{.method}} {{.path}}` [$__range]
        )
    )
)

topk(5, 
    avg_over_time (
        {app="freechat-main", level="ACCESS"}
        |= "|/api"
        | pattern "<_>|<path>::<method>|<_>|<rt>|<_>"
        | label_format api=`{{.method}} {{.path}}`
        | unwrap rt [$__range]
    ) by (api)
)

sum (
    rate (
        {app="freechat-main", level="ACCESS"}
        |= "|/api" [1m]
    )
)

avg_over_time (
    {app="freechat-main", level="ACCESS"}
    |= "|/api"
    | pattern "<_>|<_>::<_>|<_>|<rt>|<_>"
    | unwrap rt [1m]
) by ()

max_over_time (
    {app="freechat-main", level="ACCESS"}
    |= "|/api"
    | pattern "<_>|<_>::<_>|<_>|<rt>|<_>"
    | unwrap rt [1m]
) by ()

sum by (code) (
    rate (
        {app="freechat-main", level="ACCESS"}
