targets.expr:
count (
    count_over_time (
        {app="freechat-main", level="ACCESS", path="/api/v2/public/character/search"}
        | pattern `<_> [<_>] <_> <_> - <_>|<username>|<_>`
        | keep username [$__range]
    )
)

sum (
    count_over_time (
        {app="freechat-main", level="ACCESS", path="/api/v2/public/character/search"} [$__interval]
    )
) by () or vector(0)

sum (
    count_over_time (
        {app="freechat-main", level="ACCESS", path=~"/api/v2/chat/send/stream.*", method="POST"} [$__interval]
    )
) by () or vector(0)

sum (
    count_over_time (
        {app="freechat-main", level="ACCESS", path="/api/v2/character", method="POST"} [$__interval]
    )
) by () or vector(0)

topk(5, 
    sum by (name) (
        count_over_time (
            {app="freechat-main", level="PERF", class="ChatApi", method="firstPackageReceived"}
            | pattern `<_>|"<name>",<_>` [$__range]
        )
    )
)

count_over_time (
    {app="freechat-main", level="ACCESS", path=~"/api.*"}
    | keep http_code [$__range]
)

sum (
    rate (
        {app="freechat-main", level="ACCESS", path=~"/api.*"}
        | label_format code=`{{ regexReplaceAll "(\\d{1})\\d*" .http_code "${1}xx" }}`
        | keep code [1m]
    ) * 60
) by (code)

sum (
    count_over_time (
        {app="freechat-main", level="ACCESS", path=~"/api.*"} [$__range]
